import getConfig, { CCFConfig } from './Config'

let _config: CCFConfig = getConfig()

export const setConfig = (ccfConfig: CCFConfig = getConfig()) => {
  return (_config = ccfConfig)
}

export default function config(): CCFConfig {
  return _config
}
